const express = require('express');
const router = express.Router();
const userController = require('../controllers/userController');  // Ensure this file exists
const authMiddleware = require('../middleware/auth');  // Ensure this file exists for authentication

// GET /api/user/profile - Fetch the user's profile
router.get('/profile', authMiddleware, userController.getUserProfile);

// PUT /api/user/profile - Update the user's profile
router.put('/profile', authMiddleware, userController.updateUserProfile);

module.exports = router;
